import { ThemeProvider } from '@emotion/react';
import { createTheme,Container, Typography, TextField, TableContainer, LinearProgress, Table, TableHead, TableRow, TableCell, TableBody } from '@mui/material';
import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { makeStyles } from 'tss-react/mui';

const Coinstable = () => {
    const [coins, setCoins] = useState<any[]>([]);
    const [loading, setLoading] = useState(false);
    const [search, setSearch] = useState<any>("");
    const [page, setPage] = useState(1);
    const useStyles = makeStyles()((theme) => {
        return{
        row: {
          backgroundColor: "#16171a", 
          cursor: "pointer",
          "&:hover": {
            backgroundColor: "#131111",
          },
          fontFamily: "Roboto",
          color:"#fff"
        },
        pagination: {
          "& .MuiPaginationItem-root": {
            color: "aquamarine",
          }
        },
        };
      });
    const navigate = useNavigate ();
    const {classes} = useStyles() ;
    const darkTheme = createTheme({
        palette: {
          primary: {
            main: "#fff",
          },
        },
      });
    const fetchCoins = async () => {
        setLoading(true);
        const { data } = await axios.get("/v3/markets/summaries");
        console.log(data);

        setCoins(data);
        setLoading(false);
      };
      useEffect(() => {
        fetchCoins();
      }, []);

      const handleSearch = () => {
        return coins.filter(
          (coin) =>
            search == "" || coin.symbol.toLowerCase().includes(search)
        );
      };
  return (
    <ThemeProvider theme={darkTheme}>
        <Container style={{ textAlign: "center" }}>
        <Typography
          variant="h4"
          style={{ margin: 18, fontFamily: "Roboto" }}
        >
          Cryptocurrency Prices
        </Typography>
        <TextField
          label="Search For a Crypto Currency.."
          variant="outlined"
          style={{ marginBottom: 20, width: "100%" }}
          onChange={(e) => setSearch(e.target.value)}
        />
        <TableContainer>
        {loading ? (
            <LinearProgress style={{ backgroundColor: "aquamarine" }} />
          ) : (
            <Table aria-label="simple table">
            <TableHead style={{ backgroundColor: "#7fccb6" }}>
              <TableRow>
                {["Coin", "High(GBP)", "low(GBP)", "Volume", "Quote Volume", "Percentage Change", "Updated On"].map((head) => (
                  <TableCell
                    style={{
                      color: "black",
                      fontWeight: "700",
                      fontFamily: "Roboto",
                    }}
                    key={head}
                    align="center"
                  >
                    {head}
                  </TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
                    {handleSearch().map((row) => {
                    const profit = row.percentChange > 0;
                    return (
                        <TableRow
                        onClick={() => navigate(`/coins/${row.id}`)}
                        className={classes.row}
                        key={row.symbol}
                      >
                            <TableCell
                                component="th"
                                scope="row"
                                style={{
                                    display: "flex",
                                    gap: 15,
                                }}
                            >
                                <div
                                    style={{ display: "flex", flexDirection: "column" }}
                                >
                                <span
                                style={{
                                    textTransform: "uppercase",
                                    fontSize: 22,
                                }}
                                >
                              {row.symbol}
                            </span>
                          </div>
                        </TableCell>
                      </TableRow>
                    )
                    })}
            </TableBody>
            </Table>
          )}
        </TableContainer>
        </Container>
    </ThemeProvider>
  )
}

export default Coinstable
