import { ThemeProvider } from '@emotion/react';
import { createTheme,Container, Typography, TextField, TableContainer, LinearProgress } from '@mui/material';
import axios from 'axios';
import React, { useEffect, useState } from 'react'

const Coinstable = () => {
    const [coins, setCoins] = useState([]);
    const [loading, setLoading] = useState(false);
    const [search, setSearch] = useState("");
    const [page, setPage] = useState(1);
    const darkTheme = createTheme({
        palette: {
          primary: {
            main: "#fff",
          },
        },
      });
    const fetchCoins = async () => {
        setLoading(true);
        const { data } = await axios.get("/v3/markets/summaries");
        console.log(data);

        setCoins(data);
        setLoading(false);
      };
      useEffect(() => {
        fetchCoins();
      }, []);
  return (
    <ThemeProvider theme={darkTheme}>
        <Container style={{ textAlign: "center" }}>
        <Typography
          variant="h4"
          style={{ margin: 18, fontFamily: "Roboto" }}
        >
          Cryptocurrency Prices
        </Typography>
        <TextField
          label="Search For a Crypto Currency.."
          variant="outlined"
          style={{ marginBottom: 20, width: "100%" }}
          onChange={(e) => setSearch(e.target.value)}
        />
        <TableContainer>
        {loading ? (
            <LinearProgress style={{ backgroundColor: "aquamarine" }} />
          ) : (
            <></>
          )}
        </TableContainer>
        </Container>
    </ThemeProvider>
  )
}

export default Coinstable
